import React, {
  useState, useEffect, useRef, useContext,
} from 'react';
import { FiUser } from 'react-icons/fi';
import styles from './ConfigureProfile.module.scss';
import { UserContext } from '../../../containers/App';
import profileImage from '../../../assets/images/profileImage.png';
import { User } from '../../../interfaces/User';
import * as userEndpoints from '../../../endpoints/user';

const ConfigureProfile = () => {
  const [showConfiguration, setShowConfiguration] = useState(false);
  const [image, setImage] = useState<File>();
  const [hasChangedImage, setHasChangedImage] = useState(false);
  const [hasChangedDescription, setHasChangedDescription] = useState(false);
  const [description, setDescription] = useState<string | undefined>('');

  // Reads current connected user from Context
  const contextUser = useContext(UserContext);

  // Ref for the profile configuration window
  const wrapperRef = useRef<HTMLDivElement>(null);

  // Ref for the input to select picture
  const pictureRef = useRef<HTMLInputElement>(null);

  // // Changes URL and description every time the context user is updated
  // useEffect(() => {
  //   setImage(contextUser.user?.profileImage);
  //   setDescription(contextUser.user?.description!);
  // }, [contextUser.user]);

  // Closes the profile configuration window if the user clicks outside
  useEffect(() => {
    // Detects if clicked on outside of element
    const handleClickOutside = (event: MouseEvent) => {
      // As Node used since event.target cannot be inferred to be a Node. It could have other types
      if (wrapperRef.current && !wrapperRef.current.contains(event.target as Node)) {
        setShowConfiguration(false);
      }
    };
    // Bind the event listener
    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      // Unbind the event listener on clean up
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [wrapperRef]);

  const handleDescriptionChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setDescription(event.target.value);
    setHasChangedDescription(true);
  };

  const handleChangePicture = () => {
    console.log(pictureRef?.current?.files);
    const file: File = pictureRef?.current?.files![0]!;
    setImage(file);
    setHasChangedImage(true);
  };

  const onConfigurationSubmit = async (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    if (hasChangedDescription) {
      // Updates description in database
      const modifiedUser: User = { ...contextUser.user!, description };

      // Updates description in context user
      contextUser.setUser(modifiedUser);
    }
    if (hasChangedImage) {
      // Updates profile image in database
      const userToUpdate: User = { ...contextUser.user! };
      const userUpdated: User = await userEndpoints.updateImageProfile(image!, userToUpdate!._id!, 'nose');
      const imageUrl: string = await userEndpoints.getImageProfile(userUpdated.profileImage as string);

      // Updates profile image in context user
      const modifiedUser: User = { ...contextUser.user!, profileImage: imageUrl };
      contextUser.setUser(modifiedUser);
    }
  };

  const configurationForm = (
    <form onSubmit={onConfigurationSubmit}>
      <input
        type="text"
        value={contextUser?.user?.description || 'What is going on?'}
        onChange={handleDescriptionChange}
      />
      <input
        type="file"
        ref={pictureRef}
        onChange={handleChangePicture}
      />
      <button type="submit">Env√≠a</button>
      <img alt='Profile photo' style={{ marginTop: '50px', height: '200px', width: '200px' }} src={contextUser.user?.profileImage as string}></img>
    </form>
  );

  return (
    <div className={styles.configureProfileContainer}>
      <div>
        <button
          className={styles.iconButton}
          onClick={() => setShowConfiguration(true)}
        >
          <FiUser className={styles.profileIcon} />
        </button>
        <div
          ref={wrapperRef}
          className={showConfiguration ? styles.configurationTargetShow : styles.configurationTargetHide}
        >
          <div
            className={styles.imageWrapper}
            style={{
              backgroundImage: contextUser?.user?.profileImage ? `url(${contextUser?.user?.profileImage as File})` : `url(${profileImage})`,
            }}
          />
          {configurationForm}
        </div>
      </div>
    </div>
  );
};

export default ConfigureProfile;
